mapper.update:
		DECLARE
			V_APRV_NO APRV.APRV_NO%TYPE := 0;
			V_APRV_SEQ APPROVAL.APRV_SEQ%TYPE := 1;
			V_REQ_ST APRV.REQ_ST%TYPE;
			V_APRV_ST APPROVAL.APRV_ST%TYPE;
			V_CNT APPROVAL.APRV_NO%TYPE;

		BEGIN
			SELECT COUNT(APRVR)
			INTO V_CNT
			FROM APPROVAL
			WHERE APRV_NO = V_APRV_NO;

			SELECT A.REQ_ST, P.APRV_ST
			INTO V_REQ_ST, V_APRV_ST
			FROM APPROVAL P,
					APRV A
            WHERE P.APRV_NO = V_APRV_NO
				AND A.APRV_NO = P.APRV_NO
				AND P.APRV_SEQ = V_APRV_SEQ;


		IF V_CNT = V_APRV_SEQ AND V_APRV_ST = '완료' THEN
			UPDATE APRV
			SET
			REQ_ST = '완료'
			WHERE APRV_NO = V_APRV_NO;

		ELSIF V_CNT > V_APRV_SEQ AND V_APRV_ST = '완료' THEN

			UPDATE APRV
			SET
			REQ_ST = '검토'
			WHERE APRV_NO =V_APRV_NO;

			UPDATE APPROVAL
			SET
			APRV_ST = '진행'
			WHERE APRV_NO = V_APRV_NO
			AND APRV_SEQ = V_APRV_SEQ +1;

		ELSIF V_APRV_ST = '반려' THEN
			UPDATE APRV
			SET
			REQ_ST = '반려'
			WHERE APRV_NO =V_APRV_NO;
		END IF;

		END;

mapper.select:
SELECT * FROM TRUST
WHERE EMP_NO = #{empNo}
ORDER BY EDT DESC

mapper.update:
UPDATE TRUST

	RPTT = #{rptt},
	SDT = #{sdt},
	EDT = #{edt}

WHERE TRUST_NO = #{trustNo}
