mapper.update:
DECLARE
	V_APRV_NO APRV.APRV_NO%TYPE := #{aprvNo};
	V_APRV_SEQ APPROVAL.APRV_SEQ%TYPE;
	V_APRV_ST APPROVAL.APRV_ST%TYPE;
	V_CNT APPROVAL.APRV_NO%TYPE;

BEGIN
	SELECT COUNT(APRVR)
	INTO V_CNT
	FROM APPROVAL
	WHERE APRV_NO = V_APRV_NO;

	SELECT 	APRV_ST, APRV_SEQ
	INTO V_APRV_ST, V_APRV_SEQ
	FROM APPROVAL
	WHERE APRV_NO = V_APRV_NO
			AND APRVR = #{aprvr}
			OR APRV_NO = V_APRV_NO
			AND APRVR IN (SELECT EMP_NO
							FROM TRUST
							WHERE RPTT = #{aprvr});

IF V_CNT = V_APRV_SEQ AND V_APRV_ST = '완료' THEN
	UPDATE APRV
	SET
	REQ_ST = '완료'
	WHERE APRV_NO = V_APRV_NO;

ELSIF V_CNT > V_APRV_SEQ AND V_APRV_ST = '완료' THEN
	UPDATE APRV
	SET
	REQ_ST = '검토'
	WHERE APRV_NO =V_APRV_NO;

	UPDATE APPROVAL
	SET
	APRV_ST = '진행'
	WHERE APRV_NO = V_APRV_NO
		AND APRV_SEQ = V_APRV_SEQ +1;

ELSIF V_APRV_ST = '반려' THEN
	UPDATE APRV
	SET
	REQ_ST = '반려'
	WHERE APRV_NO =V_APRV_NO;
END IF;
COMMIT;
END;

SELECT 	APRVR
FROM	APPROVAL
WHERE	APRV_NO = #{aprvNo}
		AND APRV_ST = '진행'

mapper.select:
SELECT * FROM TRUST
WHERE EMP_No = #{empNo}
ORDER BY EDT DESC

mapper.update:
UPDATE EMP
   SET SIGN = #{sign}
 WHERE EMP_NO = #{empNo}

UPDATE TRUST
SET
RPTT = #{rptt},
RPTT_NM = #{rpttNm}
WHERE TRUST_NO = #{trustNo}
