mapper.update:
UPDATE APPROVAL


APRVR = #{aprvr},


APRVDTTM = #{aprvDttm},


SIGN = #{sign},


APRV_ST = #{aprvSt},


APRV_SEQ = #{aprvSeq},


REJECT = #{reject}


WHERE APRV_NO = #{aprvNo}
		AND APRVR = #{APRVR}

UPDATE REFER


	EMP_NO = #{empNo},


	REFER_NO = #{referNo},


	CHECKED = #{checked},


	REFER_APRV = #{referAprv}


WHERE APRV_NO = #{aprvNo}
	AND REFER_NO = #{referNo}

mapper.delete:
DELETE FROM REFER
WHERE APRV_NO = #{APRVNO}

	AND EMP_NO = #{empNo}


	AND REFER_APRV = #{referAprv}

mapper.select:
SELECT *
FROM APRV_LINE

WHERE dept = #{dept}


	AND EMP_NO = #{empNo}

mapper.update:
UPDATE APRV_LINE


	EMP_NO = #{empNo},


	DEPT = #{dept}


	DEFYN = #{defYn}


	AP1 = #{ap1},


	AP2 = #{ap2},


	AP3 = #{ap3},


WHERE LINE_NO = #{line_no}
