mapper.select:
SELECT APRV_NO, EMP_NO, TTL, DTTM, REQ_ST,
		DEADLINE, IMPTS
FROM APRV
WHERE EMP_NO = #{empNo}
AND REQ_ST = #{reqSt}
ORDER BY APRV_NO DESC

mapper.update:
UPDATE APRV
DTTM = SYSTIMESTAMP TTL=#{ttl}, CNTN=#{cntn}, APRV_ETC=#{aprvEtc}, DEADLINE=#{deadline}, IMPTS=#{impts}
WHERE APRV_NO = #{aprvNo} AND REQ_ST = '전송' OR '반려'

mapper.delete:
DELETE FROM APRV
WHERE APRV_NO = #{aprvNo}
AND REQ_ST = '전송' or '반려'

mapper.select:
		SELECT FORM_NO, TTL, FORM_CAT, EMP_NO, FORM_AUTH
		FROM FORM
		WHERE 

				FORM_AUTH = '공개'
				OR EMP_NO = #{empNo} 


				FORM_AUTH = '공개'
                AND FORM_CAT = #{formCat}
                OR EMP_NO= #{empNo}
                AND FORM_CAT = #{formCat}


				FORM_AUTH = '개인'
				AND EMP_NO = #{empNo}


		ORDER BY
		FORM_NO DESC

SELECT TTL, CNTN
FROM FORM
WHERE FORM_NO = #{formNo}

mapper.update:
UPDATE FORM

	TTL=#{ttl},
	CNTN=#{cntn},
	FORM_CAT=#{formCat},
	FORM_AUTH=#{formAuth}

WHERE FORM_NO = #{formNo}
