mapper.insert:

	SELECT 
	    CASE WHEN MAX(PRJ_NO) IS NULL THEN 1
	    ELSE MAX(PRJ_NO)+1 
	    END AS NO
	FROM PROJECT

begin
	insert into project
	values(${prjNo}, #{vo.nm}, #{vo.cntn}, #{vo.prog}, #{vo.st}, SYSDATE, #{vo.sdt}, #{vo.edt});


		insert into team
		values(#{list.empNo}, #{prjNo}, 

			'팀원', 


			'팀장',

			#{list.nm}, #{list.dept}
		);


end;
