mapper.update:
DECLARE
	V_FILE_NO	NUMBER(10);
BEGIN
	UPDATE POST
	SET
		TTL=#{pvo.ttl},
		CNTN=#{pvo.cntn}
	WHERE POST_NO=#{pvo.postNo};

	SELECT FILE_NO 
	INTO V_FILE_NO
	FROM FILES
	WHERE ATCH_NO = (
	 		SELECT ATCH_NO FROM POST WHERE POST_NO=#{pvo.postNo}
		);
	 
	 
		 UPDATE FILES
		 SET
		 	F_NM=#{fvo.fNm}, F_PATH=#{fvo.fPath}, F_TYPE=#{fvo.fType}
		 WHERE FILE_NO = V_FILE_NO;
	 
END;

 
		  
			SELECT DTTM 
			FROM REPLY
			WHERE REPLY_NO = #{replyNo}
		
		UPDATE REPLY
		SET CNTN = #{cntn}, DTTM = SYSDATE
		WHERE REPLY_NO = #{replyNo}

mapper.select:
SELECT E.EMP_NO, E.NM, E.ID, E.DEPT, M.POST_NO
FROM EMP E, (SELECT * 
             FROM MEMBER 
             WHERE POST_NO IN (
             	SELECT POST_NO FROM SCHEDULE WHERE PRJ_NO=#{prjNo}
             	)
             )M
WHERE E.EMP_NO = M.EMP_NO
ORDER BY POST_NO

SELECT * 
FROM FILES 
WHERE ATCH_NO in(
	SELECT ATCH_NO FROM POST WHERE PRJ_NO=#{prjNo}
	)

mapper.delete:
BEGIN
	
		DELETE FROM MEMBER 
		WHERE POST_NO = #{postNo};
		DELETE FROM SCHEDULE 
		WHERE POST_NO = #{postNo};
	
	
		DELETE FROM FILES 
		WHERE ATCH_NO = (
			SELECT ATCH_NO 
			FROM POST 
			WHERE POST_NO=#{postNo}
		);
		DELETE FROM POST 
		WHERE POST_NO = #{postNo};
	
	
		DELETE FROM FILES 
		WHERE ATCH_NO = (
			SELECT ATCH_NO 
			FROM  BUSINESS
			WHERE POST_NO=#{postNo}
		);
		DELETE FROM BUSINESS 
		WHERE POST_NO = #{postNo};
	
END;
