mapper.select:
SELECT EMP_NO, NM
FROM EMP
WHERE DEPT = #{dept}

mapper.insert:

	select pno_seq.nextval from dual

begin
	insert into schedule
		values(${postNo}, #{ttl}, #{empNo}, #{wrtr}, #{sdt}, #{edt},
			   #{place}, #{cntn}, 1, #{prjNo}, #{latitude}, #{longitude});


		insert into member(emp_no, post_no)
		values(#{no}, #{postNo});

end;
